insert into tblControlReview (seq, controlReservationSeq, score, contents, writeDate) values (seqControlReview.nextVal, 25, 8, '작년에 갔던 세비야를 화면으로 다시 만날 수 있어 기뻤습니다! 당시 가지 않았던 곳도 화면으로 보니 꼭 다시 세비야를 방문해야겠다는 생각이 드네요~ 다음에는 가이드님을 세비야에서 직접 뵙고 싶습니다~!', '2021-01-21');
